mapper.select:

select m.id as id,m.name as name,u.url as path, m.parentid as pid 
from Module m left join sys_url u on (m.urlid=u.id)
where m.parentid= #{pid} and m.Valid = 1

select m.id as id,m.name as name,u.url as path, m.parentid as pid
from Module m left join sys_url u on (m.urlid=u.id)
where m.parentid= #{pid} and m.Valid = 1 and m.name=#{name}
